#include "unity.h"

#include "solution.h"
#include <stdlib.h>

void eraseMatrix( int** m, int rows ) {
    if ( !m )
        return;

    for ( size_t i = 0; i < rows; i++ ) {
        if ( m[i] )
            free( m[i] );
    }
    free( m );
}

int** makeMatrix( const int* m, int rows, int cols ) {
    int** mat = malloc( sizeof( int* ) * rows );
    if ( !mat )
        return NULL;

    for ( size_t i = 0; i < rows; i++ ) {
        mat[i] = malloc( sizeof( int* ) * cols );
        if ( !mat[i] ) {
            eraseMatrix( mat, i );
            return NULL;
        }
        for ( size_t j = 0; j < cols; j++ ) {
            mat[i][j] = m[i * cols + j];
        }
    }

    return mat;
}

void setUp( void ) {
}

void tearDown( void ) {
}

void test_1( void ) {
    int m[]   = { 0, 1, 1, 1 };
    int rows  = 2;
    int cols  = sizeof( m ) / sizeof( int ) / rows;
    int** mat = makeMatrix( m, rows, cols );

    int expected = 1;

    int ret = maxEqualRowsAfterFlips( mat, rows, &cols );

    TEST_ASSERT_EQUAL_INT( expected, ret );

    eraseMatrix( mat, rows );
}

